import React, { useEffect, useState } from 'react';
import { signOut } from 'firebase/auth';
import { auth, db } from '../firebase';
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';
import "../dashboard.css";

export default function Dashboard(){
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [text, setText] = useState('');

  useEffect(()=>{
    loadAlerts();
  }, []);

  const loadAlerts = async () => {
    const snapshot = await getDocs(collection(db, 'alerts'));
    setItems(snapshot.docs.map(d => ({id: d.id, ...d.data()})));
  }

  const handleAdd = async () => {
    if (!text) return;
    await addDoc(collection(db, 'alerts'), { text, createdAt: new Date() });
    setText('');
    loadAlerts();
  }

  const handleLogout = async () => {
    await signOut(auth);
    navigate('/');
  }

  return (
    <div className="dashboard-page">

      <div className="dashboard-card">
        <h1 className="dashboard-title">Dashboard Banzeiro üåßÔ∏è</h1>

        <button className="dashboard-btn" onClick={handleLogout}>
          Sair da Conta
        </button>

        <h2 className="dashboard-subtitle">Criar Alerta</h2>
        <input
          className="dashboard-input"
          placeholder="Escreva um alerta..."
          value={text}
          onChange={(e)=>setText(e.target.value)}
        />
        <button className="dashboard-btn" onClick={handleAdd}>
          Adicionar Alerta
        </button>
      </div>

      <div className="dashboard-card">
        <h2 className="dashboard-subtitle">Alertas Criados</h2>

        <div className="dashboard-list">
          {items.map(i => (
            <div key={i.id} className="dashboard-item">
              {i.text}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
